import React from "react";
import { connect } from "react-redux";
import { deleteTodo, editTodo } from "../redux/action/addTodo.action";

const Todo = ({ todo, idx, deleteTodo, editTodo, selected, text }) => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
        border: "1px solid #ffffffa6",
        padding: "5px",
        cursor: "pointer"
      }}
    >
    
      <div className="movie">
        <h1>{todo.title}</h1>
        <img src="" alt="" width="300px" height="200px" />
        <p>{todo.category}</p>
        {todo.likes} <br />
        {todo.dislikes}
      </div>
      <div style={{ cursor: "pointer" }} onClick={() => deleteTodo(idx)}>x</div>
      
      <div class="flip-card">
      <div class="flip-card-front">
        <div class="inner">
          <img src="" class="icon" alt=""/>
          <h3>avengers</h3>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit. Nobis
            corrupti itaque dolore!
          </p>
        </div>
      </div>
      
    </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  deleteTodo: key => dispatch(deleteTodo(key)),
  editTodo: key => dispatch(editTodo(key))
});

const mapStateToProps = ({ text, selected }) => ({
  text,
  selected
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Todo);
