import React, { useEffect, useState } from "react";
import "./styles.css";
import TodosList from "./components/TodosList";
import { deleteAll } from "./redux/action/addTodo.action";
import { connect } from "react-redux";
import SearchBar from "./components/SearchBar";
import  todos  from "./todos";




const init = todos.category
const App = ({ deleteAll }) => {

  const [categories, setCategories] = useState([])

  const [searchField, setSearchField] = useState("")

  const onSearchChange = (e) =>{
    setSearchField(e.target.value)
  }

  const filterCategory = (state= initState)=>{
  const categories = state.todos.filter((todos) =>{
    return todos.categories.toLowerCase().includes(searchField.toLowerCase())

  // categories.filter((categories) =>{
  //   return categories.category.toLowerCase().includes(searchField.toLowerCase())
  })
} 


  useEffect(() => {
    setCategories()
  }, []);
  return (
    <div className="App">
      <h1 style={{ textDecoration: "underline" }}> Movies</h1>
      <SearchBar searchChange={onSearchChange}/>
      <TodosList todos={filterCategory}/>
      <div>
        <button
          style={{ marginTop: "20px", cursor: "pointer" }}
          onClick={deleteAll}
        >
          {" "}
          Delete All
        </button>
      </div>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  deleteAll: () => dispatch(deleteAll()),
});


export default connect(
  null,
  mapDispatchToProps
)(App);
