const initState = {
  todos: [
  // text: "",
  // selected: undefined
  {
    id: '1',
    title: 'Oceans 8',
    category: 'Comedy',
    likes: 4,
    dislikes: 1
  }, {
    id: '2',
    title: 'Midnight Sun',
    category: 'Comedy',
    likes: 2,
    dislikes: 0
  }, {
    id: '3',
    title: 'Les indestructibles 2',
    category: 'Animation',
    likes: 3,
    dislikes: 1
  }, {
    id: '4',
    title: 'Sans un bruit',
    category: 'Thriller',
    likes: 6,
    dislikes: 6
  }, {
    id: '5',
    title: 'Creed II',
    category: 'Drame',
    likes: 16,
    dislikes: 2
  }, {
    id: '6',
    title: 'Pulp Fiction',
    category: 'Thriller',
    likes: 11,
    dislikes: 3
  }, {
    id: '7',
    title: 'Pulp Fiction',
    category: 'Thriller',
    likes: 12333,
    dislikes: 32
  }, {
    id: '8',
    title: 'Seven',
    category: 'Thriller',
    likes: 2,
    dislikes: 1
  }, {
    id: '9',
    title: 'Inception',
    category: 'Thriller',
    likes: 2,
    dislikes: 1
  }, {
    id: '10',
    title: 'Gone Girl',
    category: 'Thriller',
    likes: 22,
    dislikes: 12
  },
]
};

const setPersist = todos =>
  window.localStorage.setItem("todos2", JSON.stringify(todos));

export const addTodo = (state = initState, action) => {
  switch (action.type) {
    case "PERSIST_TODOS":
      const todos = JSON.parse(window.localStorage.getItem("todos2"));
      return { ...state, todos: tod };
    case "ADD_TEXT":
      return { ...state, text: action.payload };
    case "ADD_TODO":
      const todos2 = state.todos.concat(action.payload);
      window.localStorage.setItem("todos2", JSON.stringify(todos2));
      return { ...state, todos: todos2, text: "" };
    case "DELETE_TODO":
      const todo3 = state.todos.filter((todo, i) => i !== action.payload);
      setPersist(todo3);
      return {
        ...state,
        todos: todo3
      };
    case "EDIT_TODO":
      return {
        ...state,
        text: state.todos[action.payload],
        selected: action.payload
      };
    case "EDIT_ADD_TODO":
      const todo4 = state.todos.map((todo, i) =>
        i !== action.payload.selected ? todo : action.payload.value
      );
      setPersist(todo4);
      return {
        ...state,
        todos: todo4,
        selected: undefined,
        text: ""
      };
    case "DELETE_ALL":
      setPersist([]);
      return { ...state, todos: [], text: "" };
    default:
      return state;
  }
};
