import React from "react";
import { connect } from "react-redux";
import { deleteTodo, editTodo } from "../redux/action/addTodo.action";
import closeLogo from "../x-circle-fill.svg"
import dislike from "../hand-thumbs-down-fill.svg"
import like from "../hand-thumbs-up-fill.svg"


const Todo = ({ todo, idx, deleteTodo, editTodo, selected, text }) => {
  return (
    <div  
      style={{
        margin: "15px auto",
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between",
        
        padding: "5px",
      }}
    >
    
      {/* <div className="movie">
        <h1>{todo.title}</h1>
        <img src="" alt="" width="300px" height="200px" />
        <p>{todo.category}</p>
        {todo.likes} <br />
        {todo.dislikes}
      </div>
      <div style={{ cursor: "pointer" }} onClick={() => deleteTodo(idx)}>x</div> */}
      
      <div class="flip-card col-4 col-sm-12">
        <div class="flip-card-front">
          <div style={{ cursor: "pointer", color:"red"}} onClick={() => deleteTodo(idx)}><img src={closeLogo}/> </div>

          <div class="inner">
            <img src="" class="icon" alt=""/>
            <h3 class="messagedefilant">{todo.title}</h3>
            {/* <p>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Nobis
              corrupti itaque dolore!
            </p> */}
            <p>{todo.category}</p>
          </div>
          
        </div>
        <div class="rate">
            <img width="30px" src={like}/><span> {todo.likes} </span>
             
            <img width="30px" src={dislike}/><span>{todo.dislikes}</span>
        </div>
      </div>
    </div>
   
  );
};

const mapDispatchToProps = dispatch => ({
  deleteTodo: key => dispatch(deleteTodo(key)),
  editTodo: key => dispatch(editTodo(key))
});

const mapStateToProps = ({ text, selected }) => ({
  text,
  selected
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Todo);
